class ToggleFriendship{constructor(e){this.toggler=e,this.toggleFriendship()}toggleFriendship(){console.log("inside the toggleFriendship Function"),$(this.toggler).click((function(e){e.preventDefault();let t=this;$.ajax({type:"post",url:$(t).attr("href")}).done((function(e){1==e.data.deleted?$(t).html("add"):$(t).html("remove")})).fail((function(e){e&&console.log("ERROR in friendship by ajax",e)}))}))}}